mapper.select:
SELECT * FROM ( SELECT (ROW_NUMBER() OVER()) AS RNUM, A.* FROM ( SELECT LPR_SEQ, MODEL, LOC_TYPE, LOC_SEQ, CASE WHEN LOC_TYPE = 'L' THEN (SELECT FACILITY_NAME FROM LQS_DISF_FACILITY WHERE FACILITY_SEQ = LOC_SEQ) ELSE (SELECT FARM_NAME FROM LQS_FARM_INFO WHERE FARM_SEQ = LOC_SEQ) END AS LOC_NAME, DESCRIPTION, CASE WHEN IS_ALIVE = 1 THEN true ELSE false END AS IS_ALIVE, LAST_ALIVE_DT, CRE_DT FROM LQS_LPR_INFO ORDER BY LPR_SEQ ) A
AND A.LPR_SEQ = CAST(#{lpr_seq} AS INTEGER)
) A
AND A.RNUM > (#{PAGE} - 1) * #{PAGESIZE} AND A.RNUM <= #{PAGE} * #{PAGESIZE}
